mapper.insert:
INSERT INTO kb_document (
    tenant_id, category_id, title, content, tags, sort,
    view_count, status, delete_flag, created_user, updated_user
) VALUES (
    #{tenantId}, #{categoryId}, #{title}, #{content}, #{tags}, #{sort},
    0, #{status}, 0, #{createdUser}, #{updatedUser}
)

mapper.update:
UPDATE kb_document
SET delete_flag = 1,
    updated_user = #{updatedUser}
WHERE id = #{id}
  AND delete_flag = 0

mapper.select:
SELECT *
FROM kb_document
WHERE category_id = #{categoryId}
  AND tenant_id = #{tenantId}
  AND delete_flag = 0
ORDER BY sort ASC, id DESC

mapper.update:
UPDATE kb_document
SET view_count = view_count + 1
WHERE id = #{id}
