mapper.select:
SELECT dept_id
FROM sys_user_dept
WHERE user_id = #{userId}
AND tenant_id = #{tenantId}
AND delete_flag = 0
ORDER BY main_flag DESC, id ASC

SELECT user_id
FROM sys_user_dept
WHERE dept_id = #{deptId}
AND tenant_id = #{tenantId}
AND delete_flag = 0

mapper.update:
UPDATE sys_user_dept
SET delete_flag = 1,
    version = version + 1
WHERE user_id = #{userId}
AND tenant_id = #{tenantId}
AND delete_flag = 0

UPDATE sys_user_dept
SET delete_flag = 1,
    version = version + 1
WHERE dept_id = #{deptId}
AND tenant_id = #{tenantId}
AND delete_flag = 0
